import { FC, useEffect, useState } from "react";
import "../style/weather.scss";
import axios from "axios";

const Weather: FC = () => {
  const [weatherData, setWeatherData] = useState<any>(null);
  const [location, setLocation] = useState<string>("Surat");
  const [day, setDay] = useState<number>(7);

  useEffect(() => {
    const apiKey = "4aad69c6cb244e75ac944054230506";
    // const location = "Surat"; // Example location

    const fetchWeatherData = async () => {
      try {
        const response = await axios.get(
          `http://api.weatherapi.com/v1/forecast.json?key=${apiKey}&q=${location}&days=${day}`
        );
        setWeatherData(response.data);
      } catch (error: any) {
        console.error("Error:", error.message);
      }
    };

    fetchCurrentWeatherData();
  }, []);

  useEffect(() => {
    console.log(weatherData?.forecast.forecastday[0].hour, "weatherData");
    const dayNames = weatherData?.forecast.forecastday.map((day: any) => {
      const date = new Date(day.date);
      return date.toLocaleDateString(undefined, { weekday: "long" });
    });
    console.log("dayNames:", dayNames);
  }, [weatherData]);

  if (!weatherData) {
    return <div>Loading...</div>;
  }
  return (
    <>
      <div className="weather width--full height--full-viewport">Weather</div>
    </>
  );
};

export default Weather;
