import { CategoryScale, Chart, LinearScale, PointElement } from "chart.js";
import { FC } from "react";
import { Line } from "react-chartjs-2";

interface IProps {
  lineChartData: any;
}
Chart.register(LinearScale, CategoryScale, PointElement);

const LineChart: FC<IProps> = (props) => {
  const toolTipLabel = (context: any) => {
    return `${context.raw}%`;
  };
  const toolTipTitle = () => {
    return "";
  };
  console.log(props.lineChartData, "lineChartData");

  return (
    <>
      <Line
        data={props.lineChartData}
        options={{
          plugins: {
            legend: {
              display: false,
              position: "bottom",
              labels: {
                color: "#210873",
                font: {
                  size: 20,
                  weight: "500",
                },
              },
            },
            tooltip: {
              enabled: true,
              yAlign: "bottom",
              backgroundColor: "#fff",
              bodyColor: "#000",
              titleColor: "black",
              bodyFont: {
                size: 20,
                weight: "500",
              },
              displayColors: false,
              titleMarginBottom: 20,
              boxHeight: 20,
              callbacks: {
                title: toolTipTitle,
                label: toolTipLabel,
              },
              padding: 15,
            },
          },
          scales: {
            y: {
              display: true,
              stacked: true,
              beginAtZero: true,
              max: 100,
              grace: 20,
              grid: {
                color: "white",
              },
              ticks: {
                color: "#210873",
                font: {
                  family: "Sofia-pro",
                  weight: "700",
                  size: 15,
                },
              },
            },
            x: {
              grid: {
                color: "white",
              },
              ticks: {
                color: "#210873",
                font: {
                  family: "Sofia-pro",
                  weight: "700",
                  size: 15,
                },
              },
            },
            r: {
              grid: {
                color: "white",
              },
              // pointLabels: {
              // 	color: 'black',
              // 	font: {
              // 		size: 15,
              // 		weight: '800'
              // 	}
              // },
              ticks: {
                display: false,
              },
            },
          },
          elements: {
            point: {
              backgroundColor: "white",
            },
          },
          maintainAspectRatio: false,
        }}
      />
    </>
  );
};

export default LineChart;
