import {
  CategoryScale,
  Chart,
  ChartOptions,
  LinearScale,
  PointElement,
} from "chart.js";
import { FC } from "react";
import { Line } from "react-chartjs-2";

interface IProps {
  lineChartData: any;
}
Chart.register(LinearScale, CategoryScale, PointElement);

const LineChart: FC<IProps> = (props) => {
  const toolTipLabel = (context: any) => {
    return `${context.raw}%`;
  };
  const toolTipTitle = () => {
    return "";
  };
  console.log(props.lineChartData, "lineChartData");
  const options: ChartOptions<"line"> = {
    plugins: {
      legend: {
        display: false,
        position: "bottom",
        labels: {
          color: "#210873",
          font: {
            size: 20,
            weight: "500",
          },
        },
      },
      tooltip: {
        enabled: true,
        yAlign: "bottom",
        backgroundColor: "#fff",
        bodyColor: "#000",
        titleColor: "black",
        bodyFont: {
          size: 20,
          weight: "500",
        },
        displayColors: false,
        titleMarginBottom: 20,
        boxHeight: 20,
        callbacks: {
          title: toolTipTitle,
          label: toolTipLabel,
        },
        padding: 15,
      },
    },
    scales: {
      y: {
        display: true,
        stacked: true,
        beginAtZero: true,
        max: 100,
        grace: 20,
        grid: {
          color: "white",
        },
        ticks: {
          color: "#210873",
          font: {
            family: "Sofia-pro",
            weight: "700",
            size: 15,
          },
        },
      },
      x: {
        grid: {
          color: "white",
        },
        ticks: {
          color: "#210873",
          font: {
            family: "Sofia-pro",
            weight: "700",
            size: 15,
          },
        },
      },
      r: {
        grid: {
          color: "white",
        },
        // pointLabels: {
        // 	color: 'black',
        // 	font: {
        // 		size: 15,
        // 		weight: '800'
        // 	}
        // },
        ticks: {
          display: false,
        },
      },
    },
    elements: {
      point: {
        backgroundColor: "white",
      },
    },
    maintainAspectRatio: false,
  };

  return (
    <>
      <Line
        data={
          [
            {
              axis: "y",
              label: "My First Dataset",
              data: [65, 59, 80, 81, 56, 55, 40],
              fill: false,
              backgroundColor: [
                "rgba(255, 99, 132, 0.2)",
                "rgba(255, 159, 64, 0.2)",
                "rgba(255, 205, 86, 0.2)",
                "rgba(75, 192, 192, 0.2)",
                "rgba(54, 162, 235, 0.2)",
                "rgba(153, 102, 255, 0.2)",
                "rgba(201, 203, 207, 0.2)",
              ],
              borderColor: [
                "rgb(255, 99, 132)",
                "rgb(255, 159, 64)",
                "rgb(255, 205, 86)",
                "rgb(75, 192, 192)",
                "rgb(54, 162, 235)",
                "rgb(153, 102, 255)",
                "rgb(201, 203, 207)",
              ],
              borderWidth: 1,
            },
          ] as any
        }
        options={options}
      />
    </>
  );
};

export default LineChart;
