import { FC, useEffect, useState } from "react";
import "../style/weather.scss";
import axios from "axios";
import { log } from "console";
import { getDate, getDay, getTime } from "shared/util/utility";

const Weather: FC = () => {
  const [currentWeatherData, setCurrentWeatherData] = useState<any>(null);
  const [forecastWeatherData, setForecastWeatherData] = useState<any>(null);
  const [location, setLocation] = useState<string>("Ahmedabad");
  const [day, setDay] = useState<number>(7);

  useEffect(() => {
    const apiKey = "4aad69c6cb244e75ac944054230506";
    // const location = "Surat"; // Example location

    const fetchCurrentWeatherData = async () => {
      try {
        const response = await axios.get(
          `http://api.weatherapi.com/v1/forecast.json?key=${apiKey}&q=${location}`
        );
        setCurrentWeatherData(response.data);
      } catch (error: any) {
        console.error("Error:", error.message);
      }
    };

    fetchCurrentWeatherData();
  }, []);

  useEffect(() => {
    const apiKey = "4aad69c6cb244e75ac944054230506";

    const fetchForecastWeatherData = async () => {
      try {
        const response = await axios.get(
          `http://api.weatherapi.com/v1/forecast.json?key=${apiKey}&q=${location}&days=${day}`
        );
        setForecastWeatherData(response.data);
      } catch (error: any) {
        console.error("Error:", error.message);
      }
    };

    fetchForecastWeatherData();
  }, []);

  useEffect(() => {
    console.log(currentWeatherData?.location.localtime, "currentWeatherData");

    // console.log(
    //   currentWeatherData?.forecast.forecastday[0].hour,
    //   "currentWeatherData"
    // );
    const dayNames = currentWeatherData?.forecast.forecastday.map(
      (day: any) => {
        const date = new Date(day.date);
        return date.toLocaleDateString(undefined, { weekday: "long" });
      }
    );
    console.log("dayNames:", dayNames);
  }, [currentWeatherData]);

  const time = getTime(currentWeatherData?.location.localtime);
  const date = getDate(currentWeatherData?.location.localtime);
  const days = getDay(currentWeatherData?.location.localtime);

  // if (!currentWeatherData) {
  //   return <div>Loading...</div>;
  // }
  return (
    <>
      {console.log("in")}

      <div className="weather width--full height--full-viewport">
        <div className="box flex">
          <div className="current">
            <div className="today__weather">
              <div className="flex align-items--center">
                <h3 className="text--white">
                  {currentWeatherData?.location.name}
                </h3>
                <img
                  src={currentWeatherData?.current.condition.icon}
                  alt="weather-img"
                />
              </div>
              <p className="text--white">
                Currently in {currentWeatherData?.location.name} {date}- {time},{" "}
                {days}
              </p>
              <h1 className="text--white mt--8">
                {currentWeatherData?.current.temp_c}Â°c
              </h1>
            </div>
          </div>
          <div className="forecast">
            <h5 className="text--center">7-day Forecast</h5>
          </div>
        </div>
      </div>
    </>
  );
};

export default Weather;
